import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const buttonColor = '#667eea';

const ProjectForm = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg('');
    setSuccessMsg('');

    if (!name.trim()) {
      setErrorMsg('Project name is required');
      return;
    }

    setLoading(true);
    try {
      const res = await fetch('http://localhost:8000/projects', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('jira-token')}`,
        },
        body: JSON.stringify({ name, description }),
      });

      console.log('Response status:', res.status);
      const responseBody = await res.text();
      console.log('Response body:', responseBody);

      if (!res.ok) {
        // You can try parsing JSON if possible, fallback to text
        let errorMessage = 'Failed to create project';
        try {
          const errorData = JSON.parse(responseBody);
          errorMessage = errorData.message || errorMessage;
        } catch {
          errorMessage = responseBody || errorMessage;
        }
        throw new Error(errorMessage);
      }

      setSuccessMsg('Project created successfully!');
      setTimeout(() => {
        navigate('/projects');
      }, 1500);
    } catch (error) {
      setErrorMsg(error.message || 'Failed to create project');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <h2 style={styles.header}>Create Project</h2>

      {errorMsg && <p style={styles.error}>{errorMsg}</p>}
      {successMsg && <p style={styles.success}>{successMsg}</p>}

      <label style={styles.label}>
        Project Name <span style={{ color: 'red' }}>*</span>
      </label>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        disabled={loading}
        style={styles.input}
        placeholder="Enter project name"
        required
      />

      <label style={styles.label}>Description</label>
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        disabled={loading}
        style={styles.textarea}
        placeholder="Enter project description (optional)"
      />

      <button type="submit" disabled={loading} style={styles.submitButton}>
        {loading ? 'Creating...' : 'Create'}
      </button>
    </form>
  );
};

const styles = {
  form: {
    maxWidth: 600,
    margin: '40px auto',
    padding: 30,
    backgroundColor: '#fff',
    borderRadius: 12,
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    display: 'flex',
    flexDirection: 'column',
  },
  header: {
    fontSize: 26,
    marginBottom: 25,
    color: '#333',
    textAlign: 'center',
  },
  label: {
    fontWeight: '600',
    fontSize: 16,
    color: '#444',
    marginBottom: 6,
  },
  input: {
    fontSize: 16,
    padding: '10px 12px',
    borderRadius: 6,
    border: '1px solid #ccc',
    marginBottom: 20,
  },
  textarea: {
    fontSize: 16,
    padding: '10px 12px',
    borderRadius: 6,
    border: '1px solid #ccc',
    resize: 'vertical',
    minHeight: 80,
    marginBottom: 20,
  },
  submitButton: {
    backgroundColor: buttonColor,
    color: '#fff',
    padding: '12px 18px',
    fontWeight: 600,
    fontSize: 16,
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
    boxShadow: `0 3px 8px ${buttonColor}99`,
    transition: 'background-color 0.3s ease',
    disabled: {
      opacity: 0.6,
      cursor: 'not-allowed',
    },
  },
  error: {
    color: 'red',
    fontWeight: 600,
    marginBottom: 15,
    textAlign: 'center',
  },
  success: {
    color: 'green',
    fontWeight: 600,
    marginBottom: 15,
    textAlign: 'center',
  },
};

export default ProjectForm;
