import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser ] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // Optionally, you can verify the token or fetch user data here
            // For example, you can decode the token to get user info
            const decodedUser  = JSON.parse(atob(token.split('.')[1])); // Decode JWT
            setUser (decodedUser );
        }
        setLoading(false);
    }, []);

    const login = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:8000/auth/login', {
                email,
                password,
            });
            const { access_token } = response.data;
            localStorage.setItem('token', access_token);
            const decodedUser  = JSON.parse(atob(access_token.split('.')[1])); // Decode JWT
            setUser (decodedUser );
        } catch (err) {
            setError(err.response?.data?.detail || 'Login failed');
            throw err; // Rethrow to handle in the component
        }
    };

    const googleLogin = async (tokenId) => {
        try {
            const response = await axios.get(`http://localhost:8000/auth/google/callback?token=${tokenId}`);
            const { access_token } = response.data;
            localStorage.setItem('token', access_token);
            const decodedUser  = JSON.parse(atob(access_token.split('.')[1])); // Decode JWT
            setUser (decodedUser );
        } catch (err) {
            setError(err.response?.data?.detail || 'Google login failed');
            throw err; // Rethrow to handle in the component
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setUser (null);
    };

    return (
        <AuthContext.Provider value={{ user, loading, error, login, googleLogin, logout }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
