import React from 'react';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  FaProjectDiagram, 
  FaTasks, 
  FaUsers, 
  FaSignOutAlt, 
  FaPlus,
  FaChartLine,
  FaComments,
  FaCog,
  FaEye,
  FaEdit
} from 'react-icons/fa';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const NavButton = ({ icon: Icon, label, onClick, to, variant = 'primary' }) => {
    const handleClick = () => {
      if(onClick) onClick();
      else if(to) navigate(to);
    };

    return (
      <button 
        onClick={handleClick} 
        style={{...styles.navButton, ...styles[variant]}} 
        type="button"
        aria-label={label}
      >
        <Icon style={styles.icon} />
        <span>{label}</span>
      </button>
    );
  };

  // Role-specific navigation configurations
  const getRoleNavigation = () => {
    const role = user?.role;

    switch(role) {
      case 'Admin':
        return [
          { icon: FaUsers, label: 'Manage Users', to: '/admin/users' },
          { icon: FaProjectDiagram, label: 'All Projects', to: '/admin/projects' },
          { icon: FaTasks, label: 'All Tasks', to: '/admin/tasks' },
          { icon: FaChartLine, label: 'Analytics', to: '/admin/analytics' },
          { icon: FaCog, label: 'System Settings', to: '/admin/settings' },
        ];

      case 'Project Manager':
        return [
          { icon: FaPlus, label: 'Create Project', to: '/projects/create', variant: 'secondary' },
          { icon: FaProjectDiagram, label: 'My Projects', to: '/projects/manage' },
          { icon: FaTasks, label: 'Manage Tasks', to: '/tasks/manage' },
          { icon: FaUsers, label: 'Team Members', to: '/team' },
          { icon: FaChartLine, label: 'Project Reports', to: '/reports' },
        ];

      case 'Developer':
        return [
          { icon: FaTasks, label: 'My Tasks', to: '/tasks/assigned' },
          { icon: FaProjectDiagram, label: 'Project Overview', to: '/projects/view' },
          { icon: FaEdit, label: 'Update Progress', to: '/tasks/update' },
          { icon: FaComments, label: 'Task Comments', to: '/tasks/comments' },
        ];

      case 'Client':
        return [
          { icon: FaEye, label: 'View Projects', to: '/projects/view' },
          { icon: FaChartLine, label: 'Project Progress', to: '/progress' },
          { icon: FaComments, label: 'Comments', to: '/comments' },
          { icon: FaTasks, label: 'Task Status', to: '/tasks/status' },
        ];

      default:
        return [];
    }
  };

  // Role-specific dashboard stats/info
  const getRoleDashboardInfo = () => {
    const role = user?.role;

    switch(role) {
      case 'Admin':
        return {
          title: 'Admin Dashboard',
          description: 'Manage users, projects, and system settings',
          capabilities: [
            'Full system access',
            'User management',
            'Project oversight', 
            'System analytics'
          ]
        };

      case 'Project Manager':
        return {
          title: 'Project Manager Dashboard',
          description: 'Create and manage your projects and teams',
          capabilities: [
            'Create new projects',
            'Manage project teams',
            'Assign tasks to developers',
            'Track project progress'
          ]
        };

      case 'Developer':
        return {
          title: 'Developer Dashboard',
          description: 'View and update your assigned tasks',
          capabilities: [
            'View assigned tasks',
            'Update task progress',
            'Comment on tasks',
            'View project details'
          ]
        };

      case 'Client':
        return {
          title: 'Client Dashboard',
          description: 'Monitor project progress and provide feedback',
          capabilities: [
            'View project status',
            'Monitor progress',
            'Add comments',
            'Review deliverables'
          ]
        };

      default:
        return {
          title: 'Dashboard',
          description: 'Welcome to your dashboard',
          capabilities: []
        };
    }
  };

  const navigation = getRoleNavigation();
  const roleInfo = getRoleDashboardInfo();

  return (
    <div style={styles.pageBackground}>
      <div style={styles.container}>
        <div style={styles.header}>
          <h1 style={styles.welcomeText}>
            Welcome, {user?.full_name || user?.email}
          </h1>
          <div style={styles.roleInfo}>
            <span style={styles.roleText}>
              <strong>{roleInfo.title}</strong>
            </span>
            <p style={styles.roleDescription}>{roleInfo.description}</p>
          </div>
        </div>

        {/* Role Capabilities Overview */}
        <div style={styles.capabilitiesSection}>
          <h3 style={styles.capabilitiesTitle}>Your Access Level:</h3>
          <div style={styles.capabilitiesList}>
            {roleInfo.capabilities.map((capability, index) => (
              <span key={index} style={styles.capabilityTag}>
                {capability}
              </span>
            ))}
          </div>
        </div>

        {/* Navigation */}
        <nav style={styles.nav}>
          {navigation.map((item, index) => (
            <NavButton
              key={index}
              icon={item.icon}
              label={item.label}
              to={item.to}
              onClick={item.onClick}
              variant={item.variant}
            />
          ))}

          {/* Logout button - always visible */}
          <NavButton 
            icon={FaSignOutAlt} 
            label="Logout" 
            onClick={handleLogout}
            variant="logout"
          />
        </nav>

        {/* Role-specific quick stats or notifications could go here */}
        <div style={styles.quickStats}>
          <div style={styles.statCard}>
            <h4>Quick Access</h4>
            <p>Navigate using the buttons above based on your role permissions.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

const buttonColor = '#667eea';
const secondaryColor = '#48bb78';
const warningColor = '#ed8936';
const dangerColor = '#e53e3e';

const styles = {
  pageBackground: {
    minHeight: '100vh',
    backgroundColor: '#f7fafc',
    padding: 20,
  },
  container: {
    backgroundColor: '#fff',
    padding: 30,
    borderRadius: 12,
    boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
    maxWidth: 900,
    margin: '0 auto',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  header: {
    marginBottom: 25,
    borderBottom: '2px solid #e2e8f0',
    paddingBottom: 20,
  },
  welcomeText: {
    fontSize: 32,
    color: '#2d3748',
    marginBottom: 10,
    fontWeight: 700,
  },
  roleInfo: {
    marginTop: 10,
  },
  roleText: {
    fontSize: 20,
    color: buttonColor,
    fontWeight: 600,
  },
  roleDescription: {
    fontSize: 16,
    color: '#718096',
    marginTop: 5,
    marginBottom: 0,
  },
  capabilitiesSection: {
    marginBottom: 30,
    padding: 20,
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
    border: '1px solid #e2e8f0',
  },
  capabilitiesTitle: {
    fontSize: 18,
    color: '#2d3748',
    marginBottom: 15,
    fontWeight: 600,
  },
  capabilitiesList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: 10,
  },
  capabilityTag: {
    backgroundColor: buttonColor,
    color: 'white',
    padding: '6px 12px',
    borderRadius: 16,
    fontSize: 14,
    fontWeight: 500,
  },
  nav: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: 15,
    marginBottom: 30,
  },
  navButton: {
    display: 'flex',
    alignItems: 'center',
    gap: 12,
    padding: '16px 20px',
    fontSize: 16,
    fontWeight: 600,
    color: '#fff',
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    textAlign: 'left',
    minHeight: 60,
  },
  primary: {
    backgroundColor: buttonColor,
    boxShadow: `0 3px 8px ${buttonColor}40`,
  },
  secondary: {
    backgroundColor: secondaryColor,
    boxShadow: `0 3px 8px ${secondaryColor}40`,
  },
  warning: {
    backgroundColor: warningColor,
    boxShadow: `0 3px 8px ${warningColor}40`,
  },
  logout: {
    backgroundColor: dangerColor,
    boxShadow: `0 3px 8px ${dangerColor}40`,
  },
  icon: {
    fontSize: 22,
    flexShrink: 0,
  },
  quickStats: {
    display: 'grid',
    gridTemplateColumns: '1fr',
    gap: 20,
  },
  statCard: {
    padding: 20,
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
    border: '1px solid #e2e8f0',
  },
};

export default Dashboard;
