import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const clientId = 'YOUR_GOOGLE_CLIENT_ID'; // Replace with your actual client ID

const LoginPage = () => {
  const { login, signUp } = useAuth(); // Assuming signUp is a method in your AuthContext
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isSignUp, setIsSignUp] = useState(false); // State to toggle between login and sign-up

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isSignUp) {
        // Sign-up logic
        await signUp({ email, password }); // Call signUp method
        alert('Account created successfully! You can now log in.');
        setIsSignUp(false); // Switch to login mode after successful sign-up
      } else {
        // Login logic
        login({ email, password }); // Call login method
        navigate('/dashboard'); // Navigate to dashboard after login
      }
    } catch (error) {
      alert('Operation failed: ' + error.message);
    }
  };

  const handleSuccess = async (credentialResponse) => {
    const googleToken = credentialResponse.credential;

    try {
      const res = await fetch(`http://localhost:8000/auth/google/callback?token=${googleToken}`);
      if (!res.ok) throw new Error('Login failed');

      const data = await res.json();
      login(data.user);
      localStorage.setItem('jira-token', data.access_token);
      navigate('/dashboard'); // Navigate after successful Google login
    } catch (error) {
      alert('Authentication failed: ' + error.message);
    }
  };

  const handleError = () => {
    alert('Google login failed');
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={styles.container}>
        <div style={styles.loginBox}>
          <h2 style={styles.title}>{isSignUp ? 'Create Account' : 'Welcome!'}</h2>
          <form onSubmit={handleSubmit} style={styles.form}>
            <label htmlFor="email" style={styles.label}>Email</label>
            <input
              type="email"
              id="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={styles.input}
              required
            />
            <label htmlFor="password" style={styles.label}>Password</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              required
            />
            <button type="submit" style={styles.button}>{isSignUp ? 'Sign Up' : 'Login'}</button>
          </form>
          <div style={styles.orSeparator}>OR</div>
          <GoogleLogin
            onSuccess={handleSuccess}
            onError={handleError}
            style={styles.googleButton}
          />
          <div style={styles.toggleLink}>
            {isSignUp ? (
              <span>Already have an account? <button onClick={() => setIsSignUp(false)} style={styles.linkButton}>Login</button></span>
            ) : (
              <span>Don't have an account? <button onClick={() => setIsSignUp(true)} style={styles.linkButton}>Sign Up</button></span>
            )}
          </div>
        </div>
      </div>
    </GoogleOAuthProvider>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
  },
  loginBox: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.25)',
    padding: '40px',
    maxWidth: '400px',
    width: '100%',
    boxSizing: 'border-box',
    textAlign: 'center',
  },
  title: {
    marginBottom: '30px',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'left',
  },
  label: {
    marginBottom: '8px',
    fontWeight: '600',
    color: '#555',
  },
  input: {
    padding: '12px 15px',
    marginBottom: '20px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
  },
  button: {
    padding: '14px',
    backgroundColor: '#667eea',
    color: '#fff',
    fontWeight: '700',
    fontSize: '16px',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  orSeparator: {
    margin: '20px 0',
    color: '#777',
  },
  googleButton: {
    marginTop: '10px',
  },
  toggleLink: {
    marginTop: '15px',
    color: '#555',
  },
  linkButton: {
    background: 'none',
    border: 'none',
    color: '#667eea',
    cursor: 'pointer',
    textDecoration: 'underline',
    padding: 0,
  },
};

export default LoginPage;
