import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

// It's good practice to get this from environment variables
// const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;
const clientId = '1080816176181-p23c90520lrbhc1blep9q4pak6j14ei3.apps.googleusercontent.com';

const LoginPage = () => {
  const { login } = useAuth();
  const navigate = useNavigate();

  const [isNewUser, setIsNewUser] = useState(false);
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [email, setEmail] = useState(''); // Added email field for signup
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMsg('');

    // Validation
    if (isNewUser) {
      if (!name || !email || !password || !role) {
        setErrorMsg('Please fill in all required fields.');
        setLoading(false);
        return;
      }
    } else {
      if (!name || !password) {
        setErrorMsg('Please enter both name and password.');
        setLoading(false);
        return;
      }
    }

    try {
      if (isNewUser) {
        // SIGN-UP LOGIC
        const signupData = {
          name: name.trim(),
          email: email.trim(),
          password: password,
          role: role
        };

        const res = await fetch('http://localhost:8000/auth/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            full_name: signupData.name,
            email: signupData.email,
            password: signupData.password,
            role: signupData.role
          }),
        });

        if (!res.ok) {
          const errorData = await res.json();
          const message = errorData.detail || errorData.message || 'Signup failed.';
          throw new Error(message);
        }

        // Don't need to store the response data, just show success message
        alert('Account created successfully! Please log in with your new credentials.');

        // Clear form and switch to login
        setIsNewUser(false);
        setName('');
        setEmail('');
        setPassword('');
        setRole('');

      } else {
        // TRADITIONAL LOGIN LOGIC
        const res = await fetch('http://localhost:8000/auth/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: name.trim(), // Using name for login
            password: password,
          }),
        });

        if (!res.ok) {
          const errorData = await res.json();
          const message = errorData.detail || 'Login failed. Please check your credentials.';
          throw new Error(message);
        }

        const data = await res.json();

        // Store the token (adjust based on your backend response structure)
        if (data.access_token) {
          localStorage.setItem('jira-token', data.access_token);
        } else if (data.token) {
          localStorage.setItem('jira-token', data.token);
        }

        // Update auth context with user data
        // Assuming your backend returns user data in the login response
        if (data.user) {
          login(data.user);
        } else {
          // If user data isn't returned, create a basic user object
          login({
            name: name.trim(),
            // Add other fields if available in the response
            ...data
          });
        }

        alert('Login successful! Redirecting to dashboard...');
        navigate('/dashboard', { replace: true });
      }
    } catch (error) {
      console.error('Authentication error:', error);
      setErrorMsg(error.message || 'Operation failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSuccess = async (credentialResponse) => {
    setLoading(true);
    setErrorMsg('');
    const googleToken = credentialResponse.credential;

    try {
      const res = await fetch(`http://localhost:8000/auth/google/callback?token=${googleToken}`);
      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.detail || 'Google login failed');
      }

      const data = await res.json();
      localStorage.setItem('jira-token', data.access_token);
      login(data.user);
      navigate('/dashboard', { replace: true });
    } catch (error) {
      console.error('Google auth error:', error);
      setErrorMsg('Google authentication failed: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleError = () => {
    setErrorMsg('Google login failed. Please try again.');
  };

  const handleToggleMode = () => {
    setIsNewUser(!isNewUser);
    setErrorMsg('');
    // Clear form when switching modes
    setName('');
    setEmail('');
    setPassword('');
    setRole('');
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={styles.container}>
        <h1 style={styles.titleText}>JIRA-LIKE PLATFORM</h1>

        <div style={styles.loginBox}>
          <h2 style={styles.subtitle}>{isNewUser ? 'Create Account' : 'Welcome Back!'}</h2>

          {errorMsg && <p style={styles.error}>{errorMsg}</p>}

          <form onSubmit={handleSubmit} style={styles.form}>
            <label htmlFor="name" style={styles.label}>Name</label>
            <input
              type="text"
              id="name"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              style={styles.input}
              disabled={loading}
              required
            />

            {isNewUser && (
              <>
                <label htmlFor="email" style={styles.label}>Email</label>
                <input
                  type="email"
                  id="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  style={styles.input}
                  disabled={loading}
                  required
                />

                <label htmlFor="role" style={styles.label}>Role</label>
                <select
                  id="role"
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  style={styles.input}
                  disabled={loading}
                  required
                >
                  <option value="">Select your role</option>
                  <option value="Admin">Admin</option>
                  <option value="Project Manager">Project Manager</option>
                  <option value="Developer">Developer</option>
                  <option value="Client">Client</option>
                </select>
              </>
            )}

            <label htmlFor="password" style={styles.label}>Password</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              disabled={loading}
              required
            />

            <button type="submit" style={styles.button} disabled={loading}>
              {loading ? 'Processing...' : (isNewUser ? 'Sign Up' : 'Login')}
            </button>
          </form>

          <div style={styles.orSeparator}>OR</div>

          <GoogleLogin
            onSuccess={handleGoogleSuccess}
            onError={handleError}
            disabled={loading}
          />

          <div style={styles.toggleLink}>
            {isNewUser ? (
              <span>Already have an account? 
                <button 
                  onClick={handleToggleMode} 
                  style={styles.linkButton} 
                  disabled={loading}
                  type="button"
                >
                  Login
                </button>
              </span>
            ) : (
              <span>New user? 
                <button 
                  onClick={handleToggleMode} 
                  style={styles.linkButton} 
                  disabled={loading}
                  type="button"
                >
                  Create Account
                </button>
              </span>
            )}
          </div>
        </div>
      </div>
    </GoogleOAuthProvider>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
  },
  titleText: {
    fontSize: '50px',
    fontFamily: "'Arial', sans-serif",
    color: '#fff',
    marginBottom: '40px',
  },
  loginBox: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.25)',
    padding: '40px',
    maxWidth: '400px',
    width: '100%',
    boxSizing: 'border-box',
    textAlign: 'center',
  },
  subtitle: {
    marginBottom: '30px',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'left',
  },
  label: {
    marginBottom: '8px',
    fontWeight: '600',
    color: '#555',
  },
  input: {
    padding: '12px 15px',
    marginBottom: '20px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
  },
  button: {
    padding: '14px',
    backgroundColor: '#667eea',
    color: '#fff',
    fontWeight: '700',
    fontSize: '16px',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    opacity: 1,
  },
  orSeparator: {
    margin: '20px 0',
    color: '#777',
  },
  toggleLink: {
    marginTop: '15px',
    color: '#555',
  },
  linkButton: {
    background: 'none',
    border: 'none',
    color: '#667eea',
    cursor: 'pointer',
    textDecoration: 'underline',
    padding: 0,
    marginLeft: '5px',
  },
  error: {
