import React, { useEffect, useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';

const buttonColor = '#667eea';

const ProjectList = () => {
  const { user } = useAuth();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchProjects = async () => {
    try {
      const res = await fetch('http://localhost:8000/projects', {
        headers: { Authorization: `Bearer ${localStorage.getItem('jira-token')}` },
      });
      if (!res.ok) throw new Error('Failed to fetch projects');
      const data = await res.json();
      setProjects(data);
    } catch (error) {
      alert(error.message);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  if (loading) {
    return (
      <div style={styles.container}>
        <p style={styles.message}>Loading projects...</p>
      </div>
    );
  }

  if (!projects.length) {
    return (
      <div style={styles.container}>
        <p style={styles.message}>No projects found.</p>
        {(user.role === 'Admin' || user.role === 'Project Manager') && (
          <Link to="/projects/new" style={{ textDecoration: 'none' }}>
            <button style={styles.createButton}>Create New Project</button>
          </Link>
        )}
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Projects</h2>
      <ul style={styles.projectList}>
        {projects.map((p) => (
          <li key={p.id} style={styles.projectItem}>
            <strong>{p.name}</strong>: {p.description || '-'}
          </li>
        ))}
      </ul>
      {(user.role === 'Admin' || user.role === 'Project Manager') && (
        <Link to="/projects/new" style={{ textDecoration: 'none' }}>
          <button style={styles.createButton}>Create New Project</button>
        </Link>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    maxWidth: 640,
    margin: '40px auto',
    backgroundColor: '#fff',
    borderRadius: 12,
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
  },
  message: {
    fontSize: 18,
    color: '#666',
    textAlign: 'center',
    margin: '30px 0',
  },
  header: {
    fontSize: 26,
    marginBottom: 20,
    textAlign: 'center',
  },
  projectList: {
    listStyleType: 'disc',
    paddingLeft: 20,
    marginBottom: 30,
  },
  projectItem: {
    marginBottom: 12,
    fontSize: 18,
  },
  createButton: {
    display: 'block',
    margin: '0 auto',
    padding: '12px 25px',
    fontSize: 16,
    fontWeight: 600,
    color: '#fff',
    backgroundColor: buttonColor,
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
    boxShadow: `0 3px 8px ${buttonColor}99`,
    transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
  },
};

export default ProjectList;
