import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const clientId = '1080816176181-p23c90520lrbhc1blep9q4pak6j14ei3.apps.googleusercontent.com';

const LoginPage = () => {
  const { login } = useAuth(); // Removed signUp as it's no longer needed
  const navigate = useNavigate();

  // No longer need isNewUser state
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Direct login logic using name and role
      login({ id: 1, name, role, password }); // Adjust this according to your login logic
      navigate('/dashboard', { replace: true }); // Replace the current history entry
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  const handleSuccess = async (credentialResponse) => {
    const googleToken = credentialResponse.credential;

    try {
      const res = await fetch(`http://localhost:8000/auth/google/callback?token=${googleToken}`);
      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.detail || 'Google login failed');
      }

      const data = await res.json();
      login(data.user);
      localStorage.setItem('jira-token', data.access_token);
      navigate('/dashboard', { replace: true });
    } catch (error) {
      alert('Authentication failed: ' + error.message);
    }
  };

  const handleError = () => {
    alert('Google login failed');
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={styles.container}>
        {/* Title Text */}
        <h1 style={styles.titleText}>JIRA-LIKE PLATFORM</h1>

        <div style={styles.loginBox}>
          <h2 style={styles.subtitle}>Welcome Back!</h2> {/* Changed subtitle */}
          <form onSubmit={handleSubmit} style={styles.form}>
            <label htmlFor="name" style={styles.label}>Name</label>
            <input
              type="text"
              id="name"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              style={styles.input}
              required
            />
            <label htmlFor="role" style={styles.label}>Role</label>
            <input
              type="text"
              id="role"
              placeholder="Enter your role"
              value={role}
              onChange={(e) => setRole(e.target.value)}
              style={styles.input}
              required
            />
            <label htmlFor="password" style={styles.label}>Password</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              required
            />
            <button type="submit" style={styles.button}>Login</button> {/* Changed button text */}
          </form>
          <div style={styles.orSeparator}>OR</div>
          <GoogleLogin
            onSuccess={handleSuccess}
            onError={handleError}
          />
          {/* Removed the toggleLink section */}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: '20px',
  },
  titleText: {
    fontSize: '50px',
    fontFamily: "'Arial', sans-serif",
    color: '#fff',
    marginBottom: '40px',
  },
  loginBox: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.25)',
    padding: '40px',
    maxWidth: '400px',
    width: '100%',
    boxSizing: 'border-box',
    textAlign: 'center',
  },
  subtitle: {
    marginBottom: '30px',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'left',
  },
  label: {
    marginBottom: '8px',
    fontWeight: '600',
    color: '#555',
  },
  input: {
    padding: '12px 15px',
    marginBottom: '20px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
  },
  button: {
    padding: '14px',
    backgroundColor: '#667eea',
    color: '#fff',
    fontWeight: '700',
    fontSize: '16px',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  orSeparator: {
    margin: '20px 0',
    color: '#777',
  },
  toggleLink: { // This style is no longer used but kept for completeness if needed elsewhere
    marginTop: '15px',
    color: '#555',
  },
  linkButton: { // This style is no longer used but kept for completeness if needed elsewhere
    background: 'none',
    border: 'none',
    color: '#667eea',
    cursor: 'pointer',
    textDecoration: 'underline',
    padding: 0,
  },
};

export default LoginPage;
